import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import AddUser from "./AddUser";
import { addUser } from "../features/userSlice";
import DisplayUser from "./DisplayUsers";
import "./Users.css"

const  LOCALE_STORAGE_KEY = "user/userdata";

const Users = () => {
    const userlist = useSelector((state) => state.users.value);

    useEffect(() => {
        localStorage.setItem(LOCALE_STORAGE_KEY, )
    })

    return (
        <div>
            <div className="addUser">
                <AddUser addUser={addUser}  userlist={userlist} />
            </div>

            <div className="displayUsers">
                {userlist.map((user) => <DisplayUser key={user.id} user={user} />)}
            </div>
        </div>
    );
};

export default Users;
